mapper.select:
select tr.id, u.name as owner, tr.money, tr.name, tr.expectedDate, cus.name as customerId, tr.stage, tr.type, tr.source, a.name as activityId, con.fullname as contactsId, tr.createBy, tr.createTime, tr.editBy, tr.editTime, tr.description, tr.contactSummary, tr.nextContactTime
from tbl_tran tr left join tbl_user u on tr.owner=u.id left join tbl_customer cus on tr.customerId=cus.id left join tbl_contacts con on tr.contactsId=con.id left join tbl_activity a on tr.activityId=a.id
where tr.id=#{id}

mapper.insert:
insert into tbl_tran ( id, owner, money, name, expectedDate, customerId, stage, type, source, activityId, contactsId, createBy, createTime, description, contactSummary, nextContactTime
) values ( #{id}, #{owner}, #{money}, #{name}, #{expectedDate}, #{customerId}, #{stage}, #{type}, #{source}, #{activityId}, #{contactsId}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime}
)
